tableName\"></include>\n		set \n	<#list fieldList as var>\n		<#if var[3] == \"是\">\n			${var[0]} = ${r\"#{\"}${var[0]}${r\"}\"},\n		</#if>\n	</#list>\n		${objectNameUpper}_ID = ${objectNameUpper}_ID\n		where \n		${objectNameUpper}_ID = ${r\"#{\"}${objectNameUpper}_ID${r\"}\"}\n	</update>\n	\n	<!-- 通过ID获取数据 -->\n	<select id=\"findById\" parameterType=\"pd\" resultType=\"pd\">\n		select \n		<include refid=\"Field\"></include>\n		from \n		<include refid=\"tableName\"></include>\n		where \n			${objectNameUpper}_ID = ${r\"#{\"}${objectNameUpper}_ID${r\"}\"}\n	</select>\n	\n	<!-- 列表 -->\n	<select id=\"datalistPage\" parameterType=\"page\" resultType=\"pd\">\n		select\n		<include refid=\"Field\"></include>\n		from \n		<include refid=\"tableName\"></include>\n		where 1=1\n		<if test=\"pd.keywords!= null and pd.keywords != \'\'\"><!-- 关键词检索 -->\n			and\n				(\n				<!--	根据需求自己加检索条件\n					字段1 LIKE CONCAT(CONCAT(\'%\', ${r\"#{pd.keywords})\"},\'%\')\n					 or \n					字段2 LIKE CONCAT(CONCAT(\'%\', ${r\"#{pd.keywords})\"},\'%\') \n				-->\n				)\n		</if>\n	</select>\n	\n	<!-- 列表(全部) -->\n	<select id=\"listAll\" parameterType=\"pd\" resultType=\"pd\">\n		select\n		<include refid=\"Field\"></include>\n		from \n		<include refid=\"tableName\"></include>\n	</select>\n	\n	<!-- 批量删除 -->\n	<delete id=\"deleteAll\" parameterType=\"String\">\n		delete from\n		<include refid=\"tableName\"></include>\n		where \n			${objectNameUpper}_ID in\n		<foreach item=\"item\" index=\"index\" collection=\"array\" open=\"(\" separator=\",\" close=\")\">\n                 ${r\"#{item}\"}\n		</foreach>\n	</delete>\n	\n	<!-- kuang 767375210 -->\n</mapper>\n'),('63876d5565754d2581fe3cbd6fd2904c','createSoCode','controllerTemplate','2018-01-04 20:58:06','package com.fh.controller.${packageName}.${objectNameLower};\n\nimport java.io.PrintWriter;\nimport java.text.DateFormat;\nimport java.text.SimpleDateFormat;\nimport java.util.ArrayList;\nimport java.util.Date;\nimport java.util.HashMap;\nimport java.util.List;