/*Table structure for table `t_rule` */

CREATE TABLE `t_rule` (
  `rule_id` varchar(50) NOT NULL,
  `f_product_id` varchar(20) NOT NULL COMMENT '产品编码',
  `f_rule_set` varchar(25) NOT NULL COMMENT '规则集编码',
  `f_biz_group` varchar(40) NOT NULL COMMENT '业务组名称(仅供查询展示)',
  `f_order` double(4,1) NOT NULL COMMENT '序号(用于排序)',
  `f_rule_expression` varchar(500) NOT NULL COMMENT '布尔规则表达式',
  `f_true_value` varchar(100) DEFAULT '' COMMENT '规则结果为true时要设置的值,#开头表示是子表达式,否则是常量值',
  `f_false_value` varchar(100) DEFAULT '' COMMENT '规则结果为false时要设置的值,#开头表示是子表达式,否则是常量值',
  `f_value_type` varchar(10) DEFAULT '' COMMENT 'value字段的数据类型,支持Boolean，String，Long，Double',
  `f_input_key` varchar(25) DEFAULT '' COMMENT '输入变量名称',
  `f_output_key` varchar(25) DEFAULT '' COMMENT '输出变量名称',
  `f_inner_group` varchar(25) DEFAULT '' COMMENT '内部分组名(用于设置互斥)',
  `f_exclusive` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否互斥(同内部分组,0不互斥,1互斥)',
  `f_ignore` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否忽略通过结果(0正常,1忽略)',
  `f_excption_way` tinyint(2) NOT NULL DEFAULT '0' COMMENT '规则项执行异常时策略(0异常退出,1规则通过,2规则拒绝)',
  `f_enable` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否启用(0不启用，1启用)',
  `f_remark` varchar(500) NOT NULL COMMENT '规则项详细描述',
  `f_provider` varchar(25) DEFAULT '' COMMENT '数据预处理类(征信数据提供者)',
  `f_provider_runagain` tinyint(1) DEFAULT '0' COMMENT '是否重复执行预处理类(0否,1是,当之前已调用过相同预处理时)',
  `f_provider_fail_way` tinyint(2) DEFAULT '0' COMMENT '预处理失败时的策略(0当数据为空处理,1规则通过,2规则拒绝)',
  `f_afterHandler` varchar(25) DEFAULT '' COMMENT '规则项后续处理类(例如添加黑名单等逻辑)',
  PRIMARY KEY (`rule_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='规则项配置表';

