mapper.select:
select po.PROJECT_NAME, po.DETAILS, po.CLIENT, po.PROVINCE_ID, po.PROVINCE_NAME, po.AREA_ID, po.AREA_NAME, po.BUSINESS_ID, po.BUSINESS_NAME, po.PROJECT_TYPE, po.BUDGET, po.OPPONENT, po.BID_TIME, po.MODEL_ID, po.MODEL_NAME, po.BID_NUM, po.LEADER_NAME, po.LEADER_PHONE, po.BZ, po.CREATE_TIME, po.USER_ID, po.USERNAME, po.PROJECT_ID, po.PURCHASE_ORDER, pa.STATUS from po left join tb_projectapply pa on po.PROJECT_ID = pa.PROJECT_ID where 1=1
and ( po.PROJECT_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or po.PROVINCE_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and po.CREATE_TIME >= #{pd.lastStart}
and po.CREATE_TIME <= #{pd.lastEnd}
and po.USER_ID = #{pd.USER_ID}
and po.AREA_ID in
#{pd.AREA[${index}]}
order by po.CREATE_TIME desc

select po.PROJECT_NAME, po.PROJECT_ID, pa.STATUS, po.PROJECT_TYPE, po.USER_ID from po left join tb_projectapply pa on po.PROJECT_ID = pa.PROJECT_ID where 1=1
and po.USER_ID = #{pd.USER_ID}
and po.AREA_ID in
#{pd.AREA[${index}]}
order by po.CREATE_TIME desc

select PROJECT_ID,PROJECT_NAME from
where 1=1
and USER_ID = #{pd.USER_ID}
and AREA_ID in
#{pd.AREA[${index}]}
order by CREATE_TIME desc
